sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var responseObj = {};
	var allDevices=[];
	var pathParamsVar = request.pathParams;
	var companyid = pathParamsVar.companyid;

	//Get a GlideRecord to start things up
    var grAsset = new GlideRecord('x_282518_pmps_migr_asset');
	grAsset.addQuery('company',companyid);

	grAsset.query();
	while (grAsset.next()) { 
		var device = {
			Description: grAsset.getValue('description'),
			DeviceID: grAsset.getValue('deviceid'),
			SerialNumber: grAsset.getValue('serial_number'),
			CurrentSticker: grAsset.getValue('currentsticker'),
			AssetTag: grAsset.getValue('asset_tag'),
			sys_id: grAsset.getValue('sys_id')
		};

		allDevices.push(device);
	}

	responseObj.devices = allDevices;

	response.setBody(responseObj);

})(request, response);
